CREATE TABLE `knightrecipe` (
  `rec_id` int(11) NOT NULL default '0',
  `makes` int(11) NOT NULL default '0',
  `item` int(11) NOT NULL default '0',
  `qty` int(11) NOT NULL default '0',
  `multiplier` int(11) NOT NULL default '0',
  INDEX  (`makes`)
); 

CREATE TABLE `knightrecch` (
  `rec_name` varchar(40) NOT NULL default '',
  `rec_id` int(11) NOT NULL default '0',
  `rec_item` int(11) NOT NULL default '0',
  `level` int(3) NOT NULL default '0',
  `makes` int(11) NOT NULL default '0',
  `chance` int(11) NOT NULL default '0',
  `multiplier` int(11) NOT NULL default '0',
  `xml_id` int,
  PRIMARY KEY  (`rec_id`)
); 

CREATE TABLE  `knightdungeon` (
  `area_name` varchar(40) NOT NULL DEFAULT '',
  `dungeon_name` varchar(40) NOT NULL DEFAULT '',
  `sub_name` varchar(40) NOT NULL DEFAULT '',
  `mapname` varchar(20) NOT NULL DEFAULT '0',
  `xmin` int(11) NOT NULL DEFAULT '0',
  `xmax` int(3) NOT NULL DEFAULT '0',
  `ymin` int(11) NOT NULL DEFAULT '0',
  `ymax` int(11) NOT NULL DEFAULT '0',
  `zmin` int(11) NOT NULL DEFAULT '0',
  `zmax` int(11) NOT NULL DEFAULT '0',
  `mapx` int(11) NOT NULL DEFAULT '0',
  `mapy` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`area_name`,`dungeon_name`,`sub_name`)
);


CREATE TABLE `knighttrust` (
  `account_name` varchar(45) NOT NULL default '',
  `char_name` varchar(35) NOT NULL default '',
  `level` decimal(11,0) NOT NULL default '0',
  `race` varchar(15) NOT NULL default '',
  `class` varchar(35) NOT NULL default '',
  PRIMARY KEY  (`char_name`) 	);

CREATE TABLE `knightskills` (
  `skill_id` int(11) NOT NULL default '0',
  `name` varchar(50),
  INDEX  (`skill_id`)
); 

CREATE TABLE `knightstats` (            
          `date` int(20) default NULL,          
          `hour` int(2) default NULL,           
          `period` int(2) default NULL,         
          `maxplayers` int(6) default NULL,     
          `count` decimal(8,2) default NULL,    
